public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		String str = (a < 20) ? ("a is less than 20") : 
            ((a > 20) ? ("a is more than 20") : ("a is equal to 20"));
        System.out.println(str);

		

	}

}
