public class Conditional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 10;
		 if (a < 20) 
		 {
           System.out.println("a is less than 20");
         } 
		 else if (a > 20)
		 {
           System.out.println("a is more than 20");
         } 
		 else 
		 {
           System.out.println("a is equal to 20");
         }

	}

}
